import React from 'react';
import axios from "axios";
import Userinput from "./languages";
import Show from "./show";

class App extends React.Component {

  state = {
    type: undefined,
    url: undefined,
    created: undefined,
    company: undefined,
    company_domain: undefined,
    location: undefined,
    job_title: undefined,
    description: undefined,
    logo: undefined,
    apply: undefined 
  }

  get_data = async (e) => {

    e.preventDefault();

    const language = e.target.elements.skill.value;

    axios.get(`https://jobs.github.com/positions.json?description=${language}`)
      .then((result) => {

        if (language === true) {

          console.log(result);

          this.setState({

            type: result.type,
            url: result.url,
            created_at: result.created_at,
            company: result.company,
            company_url: result.company_url,
            location: result.location,
            job_title: result.job_title,
            description: result.description,
            company_logo: result.company_logo,
            how_to_apply: result.how_to_apply,
            error: ""
          })
        }
    })

    // await fetch(`https://jobs.github.com/positions.json?description=${language}`)
    //   .then(res => res.json())
    //   .then(data => console.log(data))
  };


  render() {

    return(
      <div>

        <h2>What Programming language are you looking a Job in?</h2>

        <Userinput get_data={this.get_data}/>

        <Show 
          type = {this.state.type}
          url = {this.state.url}
          created_at = {this.state.created}
          company = {this.state.company}
          company_url = {this.state.company_url}
          location = {this.state.location}
          job_title = {this.state.job_title}
          description = {this.state.description}
          company_logo = {this.state.company_logo}
          how_to_apply = {this.state.how_to_apply}
          error = {this.state.error}
        />


      </div>
    );
  }
};


export default App;
