import React from 'react';

import Userinput from "./languages";

class App extends React.Component {

  get_data = async (e) => {

    e.preventDefault();

    const language = e.target.elements.name.skill.value;

    const _api_ = await fetch(`https://jobs.github.com/positions.json?description=${language}`);

    console.log(_api_);
  }

  render() {

    return(
      <div>

        <h2>What Programming language are you looking a Job in?</h2>

        <Userinput get_data={this.get_data}/>


      </div>
    );
  }
};


export default App;
