import React from "react";

const Jobs = props => (

    <div className="container">
        <div className="row">

            {
                props.jobs.map((job) => {

                    return (

                        <div className="col-md-4" key={job.id}>

                            <div className="recipes__box">

                                <img className="recipes__box-img" src={job.company_logo} alt={job.company} />

                                <span>  </span>

                                <div className="recipe__text">

                                    <h5 className="recipes__title"><b>Company:</b> 

                                        { job.company.length < 20 ? `${ job.title }` : `${job.title.substring(0, 25)}...` }

                                    </h5>

                                    <hr />

                                    <h5 className="recipes__subtitle"><b>Title:</b> <span>
                                        { job.title }
                                    </span></h5>

                                </div>

                                <button className="recipe_buttons"> View Job</button>

                            </div>

                            <hr />

                        </div>

                    );
                }) 
            }

        </div>

    </div>
);

export default Jobs;
