import React from 'react';
import axios from "axios";
import Userinput from "./languages";

class App extends React.Component {

  state = {
    type: undefined,
    url: undefined,
    created: undefined,
    company: undefined,
    company_domain: undefined,
    location: undefined,
    job_title: undefined,
    description: undefined,
    apply: undefined 
  }

  get_data = async (e) => {

    e.preventDefault();

    const language = e.target.elements.skill.value;

    axios.get(`https://jobs.github.com/positions.json?description=${language}`)
    .then((res) => {
      const result = res.data[0];
      console.log(result);

      this.setState({
        type: result.type,
        url: result.url,
        created: result.created_at,
        company: result.company,
        company_domain: result.company_url,
        location: result.location,
        job_title: result.job_title,
        description: result.description,
        apply: res 
      })
    })

  //   await fetch(`https://jobs.github.com/positions.json?description=${language}`)
  //     .then(res => res.json())
  //     .then(data => console.log(data))
  // };
  }

  render() {

    return(
      <div>

        <h2>What Programming language are you looking a Job in?</h2>

        <Userinput get_data={this.get_data}/>


      </div>
    );
  }
};


export default App;
